const dashboard = document.getElementById("dashboard");

document.getElementById("next-button").addEventListener("click", () => {
    if (currentPanel < totalPanels - 1) {
        currentPanel++;
        updateDashboardPosition();
    }
});

document.getElementById("prev-button").addEventListener("click", () => {
    if (currentPanel > 0) {
        currentPanel--;
        updateDashboardPosition();
    }
});

function updateDashboardPosition() {
    const translateX = -currentPanel * 100; // Translate based on current panel
    dashboard.style.transform = `translateX(${translateX}vw)`;
}

// Adding functionality to swipe left/right for mobile users
let startX = 0;
let endX = 0;

document.addEventListener("touchstart", (event) => {
    startX = event.touches[0].clientX;
});

document.addEventListener("touchmove", (event) => {
    endX = event.touches[0].clientX;
});

document.addEventListener("touchend", () => {
    if (startX > endX + 50) {
        // Swiped left
        if (currentPanel < totalPanels - 1) {
            currentPanel++;
            updateDashboardPosition();
        }
    } else if (startX + 50 < endX) {
        // Swiped right
        if (currentPanel > 0) {
            currentPanel--;
            updateDashboardPosition();
        }
    }
});

